mapper.update:
update sms_task_item set
id=id,task_id=#{taskId},service_type=#{serviceType},receiver_type=#{receiverType},receiver_id=#{receiverId},receiver=#{receiver},
vehicle_id=#{vehicleId},send_status=#{sendStatus},fail_msg=#{failMsg},msisd=#{msisd},vin=#{vin}, send_time=#{sendTime},
update_by=#{updateBy}, update_time=#{updateTime}

    id = #{id}

mapper.select:
select
, gst.template_id, gst.template_name ,gst.sms_content, gst.status, gst.remarks
from
sms_task_item gsti
inner join sms_task gst on gst.id = gsti.task_id

    gst.status = 0




        and gsti.task_id = #{taskId}


        and gst.template_id = #{templateId}


        and gst.service_type = #{serviceType}


        and gst.sms_content = #{smsContent}


        and gst.status = #{status}


        and gsti.send_status = #{sendStatus}


        and gsti.msisd like concat('%',#{msisd},'%')


        and gst.add_all = #{addAll}


        and gsti.send_time between #{beginTime} and #{endTime}


        and gsti.iccid is not null


        and gsti.iccid is null


order by gsti.create_time desc

select
from sms_task_item gsti
and gsti.task_id = #{taskId}

mapper.delete:
delete from  sms_task_item where
id in

    #{id}

mapper.select:
select sv.id as VehicleId, sv.vin, sim.msisd, stmu.iccid from sys_vehicle sv left join sys_term_model_unit stmu on stmu.id = sv.term_id left join sys_sim_management sim on stmu.iccid = sim.iccid
sv.id in
#{id}

select sum(case when send_status = 3 then 1 else 0 end) successTotal, count(*) total
from sms_task_item
where task_id = #{taskId}

select count(*) total
from sms_task_item
where task_id = #{taskId}

select gsti.id, sv.vin, sv.license_plate, sv.inter_no, svm.veh_model_name, stm.term_model_name,sim.iccid, sut.`name` as oper_unit_name,saa.`name` as oper_area_name, gsti.msisd, gsti.send_status, gsti.fail_msg from sms_task_item gsti left join sys_vehicle sv on gsti.vehicle_id = sv.id left join sys_veh_model svm on svm.id = sv.veh_model_id left join sys_term_model_unit stmu on stmu.id = sv.term_id left join sys_term_model stm on stm.id = stmu.sys_term_model_id left join sys_sim_management sim on stmu.iccid = sim.iccid left join sys_unit su on su.id = stm.unit_id left join sys_area saa on saa.id = sv.oper_area_id left join sys_unit sut on sut.id = sv.oper_unit_id
and gsti.task_id = #{taskId}
and gsti.send_status = #{sendStatus}
and gsti.msisd like concat('%',#{msisd},'%')
and sv.vin LIKE concat('%',#{vin},'%')
order by gsti.create_time desc
