mapper.update:
update sms_task set
sms_content=#{smsContent}, variables=#{variables}, add_all=#{addAll},
status=#{status},remarks=#{remarks},update_by=#{updateBy},update_time=#{updateTime}

    id = #{id}

mapper.select:
select
DISTINCT 
from
sms_task gst
inner join sms_task_item gsti on gst.id = gsti.task_id





        and gst.service_type = #{serviceType}


        and gst.template_id = #{templateId}


        and gst.template_name like concat('%',#{templateName},'%')


        and gsti.receiver like concat('%',#{receiver},'%')


        and gst.update_by like concat('%',#{updateBy},'%')


        and gst.status = #{status}


        and gst.update_time between #{beginTime} and #{endTime}


order by gst.create_time desc

select gsti.id ,gsti.task_id ,gsti.service_type ,gsti.receiver_type ,gsti.receiver_id ,gsti.receiver ,gsti.vehicle_id ,gsti.send_status ,gsti.fail_msg ,gsti.msisd, gst.template_name ,gst.sms_content, gst.update_by ,gst.update_time, gst.status from sms_task gst inner join sms_task_item gsti on gst.id = gsti.task_id
gst.id = #{taskId}
and gsti.msisd like concat('%', #{msisd}, '%')
and gsti.send_status = #{sendStatus}

select
DISTINCT 
from
sms_task gst
inner join sms_task_item gsti on gst.id = gsti.task_id
inner join sys_vehicle sv on sv.id = gsti.vehicle_id





        and gst.service_type = #{serviceType}


        and gst.template_id = #{templateId}


        and gst.status = #{status}



        and sv.vin like CONCAT('%',#{vin},'%')



        and sv.license_plate like CONCAT('%',#{licensePlate},'%')



        and sv.veh_model_id = #{vehModelId}



        and sv.inter_no like CONCAT('%',#{interNo},'%')


        and gst.update_by like concat('%',#{updateBy},'%')


        and gst.update_time between #{beginTime} and #{endTime}


order by gst.create_time desc

select gst.template_name, vm.veh_model_name, gst.update_by, gst.create_time, sv.license_plate, sv.vin, gsti.msisd, gsti.send_status, gsti.fail_msg from sms_task gst inner join sms_task_item gsti on gst.id = gsti.task_id inner join sys_vehicle sv on sv.id = gsti.vehicle_id left join sys_veh_model vm on sv.veh_model_id = vm.id
and gsti.task_id = #{taskId}
and gsti.send_status = #{sendStatus}
and gsti.msisd like concat('%',#{msisd},'%')
and sv.vin LIKE concat('%',#{vin},'%')
