mapper.select:
select

from
sys_push_batch_detail ppbd
WHERE ppbd.batch_id=#{batchId}
AND ppbd.form_name=#{formName}

select
from sys_push_batch_detail ppbd WHERE ppbd.form_id=#{formId}
ORDER BY ppbd.create_time DESC LIMIT 1

mapper.update:
update sys_push_batch_detail set
id=id,batch_id=#{batchId},form_id=#{formId},form_name=#{formName},forward_vehicle_id = #{forwardVehicleId},push_status=#{pushStatus},error_info=#{errorInfo}, audit_status=#{auditStatus}, audit_message=#{auditMessage},
update_time=#{updateTime},update_by=#{updateBy}

    id = #{id}

mapper.select:
select ppbd.id ,ppbd.batch_id ,ppbd.form_id, ppbd.form_name, ppbd.forward_vehicle_id, ppbd.push_status, ppbd.error_info , ppbd.audit_status, ppbd.audit_message, ppbd.create_time, ppbd.create_by ,ppbd.update_time ,ppbd.update_by, pb.batch_num, pb.batch_type, sv.vin from sys_push_batch_detail ppbd LEFT JOIN sys_push_batch pb ON pb.id = ppbd.batch_id LEFT JOIN sys_vehicle sv ON ppbd.form_id = sv.id
and pb.batch_type = #{batchType}
and ppbd.audit_status = #{auditStatus}
