mapper.delete:
delete from
sys_vehicle_drive_device_lk


        AND id = #{id}


        AND vehicle_id = #{vehicleId}

mapper.select:
select dmm.`name` drive_motor_model_name, dmm.rated_power, dmm.prod_unit_id, sdd.code as drvie_device_code, em.name engine_model_name, em.fuel_form, sdd.drive_model_id, sdd.install_position , sdd.sequence_number, sdd.invoice_no, sdd.engine_model_id,
from sys_vehicle_drive_device_lk svedlk left join sys_drive_device sdd on sdd.id = svedlk.drvie_device_id LEFT JOIN sys_drive_motor_model dmm ON dmm.id = sdd.drive_model_id left join sys_engine_model em on em.id = sdd.engine_model_id
AND svedlk.vehicle_id = #{vehicleId}
AND sdd.model_type = #{modelType}

select svedlk.vehicle_id, group_concat(dmm.`name`) as driveMotorModelName, group_concat(sdd.invoice_no) as invoiceNo, group_concat(p.`name`) as prodUnitName, group_concat(dmm.rated_power) as ratedPowerStr from sys_vehicle_drive_device_lk svedlk left join sys_drive_device sdd on sdd.id = svedlk.drvie_device_id LEFT JOIN sys_drive_motor_model dmm ON dmm.id = sdd.drive_model_id left join sys_engine_model em on em.id = sdd.engine_model_id inner join sys_vehicle sv on sv.id = svedlk.vehicle_id LEFT JOIN sys_veh_model svm ON svm.id = sv.veh_model_id LEFT JOIN sys_veh_notice svn ON svn.id = svm.notice_id LEFT JOIN sys_veh_type svt ON svt.id = svn.veh_type_id LEFT JOIN sys_vehicle_real_status svrs ON svrs.vehicle_id = sv.id LEFT JOIN sys_area sa on sa.id = sv.oper_license_city_id left join sys_unit p on p.id = dmm.prod_unit_id
sv.is_delete = 0 and sdd.model_type=1
and sv.vin in
#{item}
and sv.id = #{id}
and sv.vin like CONCAT('%',#{vin},'%')
and sv.license_plate LIKE CONCAT('%',#{licensePlate},'%')
and sv.oper_use_for = #{operUseFor}
and sv.veh_model_id = #{vehModelId}
and sv.sell_license_reg_date >= #{startDate}
and sv.sell_license_reg_date <= #{endDate}
and sv.manu_unit_id = #{manuUnitId}
and sa.path LIKE CONCAT('%/',#{operLicenseCityId},'/%')
and sv.subsidy_apply_status = #{subsidyApplyStatus}
group by sv.id desc
