mapper.delete:
delete from
sys_vehicle_engery_device_lk


        AND id = #{id}


        AND vehicle_id = #{vehicleId}

mapper.select:
select bdm.name battery_model_name, bdm.battery_type, bdm.total_elec_capacity, bdm.capacity_density, bdm.battery_cell_model, bdm.battery_cell_unit_id, bdm.unit_id, scm.name capacity_model_name, scm.unit_id scm_unit_id, scm.total_capacity, scm.capacity_density scm_capacity_density, sed.`name` AS engery_device_name,sed.battery_model_id,sed.capacity_model_id,sed.model_type,sed.install_postion, sed.invoice_no, svedlk.id,svedlk.create_time,svedlk.create_by,svedlk.vehicle_id,svedlk.engery_device_id from sys_vehicle_engery_device_lk svedlk LEFT JOIN sys_engery_device sed on sed.id = svedlk.engery_device_id LEFT JOIN sys_battery_device_model bdm ON bdm.id = sed.battery_model_id LEFT JOIN sys_super_capacitor_model scm ON scm.id = sed.capacity_model_id
and svedlk.vehicle_id = #{vehicleId}
and svedlk.engery_device_id = #{engeryDeviceId}

select

from
sys_vehicle_engery_device_lk svedlk
where svedlk.vehicle_id = #{vehicleId}
and svedlk.engery_device_id = #{engeryDeviceId}

select svedlk.vehicle_id, group_concat(bdm.battery_cell_model) as batteryCellModel, group_concat(mt.`name`) as modelTypeDisplay, group_concat(bcu.`name`) as batteryCellUnitName, group_concat(btd.`name`) as batteryTypeName, group_concat(sed.invoice_no) as invoiceNo, group_concat( case when sed.model_type = 1 then bdm.name else scm.name end ) as modelName, group_concat( case when sed.model_type = 1 then bdm.total_elec_capacity else scm.total_capacity end ) as totalCapacityStr, group_concat( case when sed.model_type = 1 then bdm.capacity_density else scm.capacity_density end ) as capacityDensityStr, group_concat( case when sed.model_type = 1 then u.`name` else scmu.`name` end ) as unitName from sys_vehicle_engery_device_lk svedlk LEFT JOIN sys_engery_device sed on sed.id = svedlk.engery_device_id LEFT JOIN sys_battery_device_model bdm ON bdm.id = sed.battery_model_id LEFT JOIN sys_super_capacitor_model scm ON scm.id = sed.capacity_model_id inner join sys_vehicle sv on sv.id = svedlk.vehicle_id LEFT JOIN sys_veh_model svm ON svm.id = sv.veh_model_id LEFT JOIN sys_veh_notice svn ON svn.id = svm.notice_id LEFT JOIN sys_veh_type svt ON svt.id = svn.veh_type_id LEFT JOIN sys_vehicle_real_status svrs ON svrs.vehicle_id = sv.id LEFT JOIN sys_area sa on sa.id = sv.oper_license_city_id LEFT JOIN sys_dict mt on mt.val = sed.model_type LEFT JOIN sys_dict btd on btd.val = bdm.battery_type left join sys_unit bcu on bcu.id = bdm.battery_cell_unit_id left join sys_unit u on u.id = bdm.battery_cell_unit_id left join sys_unit scmu on scmu.id = scm.unit_id
sv.is_delete = 0 and mt.type = 'ENERGY_DEVICE_TYPE' and btd.type = 'BATTERY_TYPE'
and sv.vin in
#{item}
and sv.id = #{id}
and sv.vin like CONCAT('%',#{vin},'%')
and sv.license_plate LIKE CONCAT('%',#{licensePlate},'%')
and sv.oper_use_for = #{operUseFor}
and sv.veh_model_id = #{vehModelId}
and sv.sell_license_reg_date >= #{startDate}
and sv.sell_license_reg_date <= #{endDate}
and sv.manu_unit_id = #{manuUnitId}
and sa.path LIKE CONCAT('%/',#{operLicenseCityId},'/%')
and sv.subsidy_apply_status = #{subsidyApplyStatus}
group by sv.id desc
