mapper.delete:
delete from
sys_vehicle_power_device_lk


        AND id = #{id}


        AND vehicle_id = #{vehicleId}

mapper.select:
select fsm.`name` fuel_battery_model_name, fsm.rated_power, fsm.prod_unit_id, spd.code as power_device_code, spd.install_position, spd.fuel_generator_model_id, spd.invoice_no,
from sys_vehicle_power_device_lk svedlk LEFT JOIN sys_power_device spd on spd.id = svedlk.power_device_id LEFT JOIN sys_fuel_system_model fsm ON fsm.id = spd.fuel_battery_model_id
AND svedlk.vehicle_id = #{vehicleId}
AND spd.model_type = #{modelType}

select svedlk.vehicle_id, group_concat(fsm.`name`) as fuelBatteryModelName, group_concat(spd.invoice_no) as invoiceNo, group_concat(p.`name`) as prodUnitName, group_concat(fsm.rated_power) as ratedPowerStr from sys_vehicle_power_device_lk svedlk LEFT JOIN sys_power_device spd on spd.id = svedlk.power_device_id LEFT JOIN sys_fuel_system_model fsm ON fsm.id = spd.fuel_battery_model_id inner join sys_vehicle sv on sv.id = svedlk.vehicle_id LEFT JOIN sys_veh_model svm ON svm.id = sv.veh_model_id LEFT JOIN sys_veh_notice svn ON svn.id = svm.notice_id LEFT JOIN sys_veh_type svt ON svt.id = svn.veh_type_id LEFT JOIN sys_vehicle_real_status svrs ON svrs.vehicle_id = sv.id LEFT JOIN sys_area sa on sa.id = sv.oper_license_city_id left join sys_unit p on p.id = fsm.prod_unit_id
sv.is_delete = 0 and spd.model_type=2
and sv.vin in
#{item}
and sv.id = #{id}
and sv.vin like CONCAT('%',#{vin},'%')
and sv.license_plate LIKE CONCAT('%',#{licensePlate},'%')
and sv.oper_use_for = #{operUseFor}
and sv.veh_model_id = #{vehModelId}
and sv.sell_license_reg_date >= #{startDate}
and sv.sell_license_reg_date <= #{endDate}
and sv.manu_unit_id = #{manuUnitId}
and sa.path LIKE CONCAT('%/',#{operLicenseCityId},'/%')
and sv.subsidy_apply_status = #{subsidyApplyStatus}
group by sv.id desc
